import React from 'react'
import { useState } from 'react'
import { Stack, HStack, VStack, Button, useToast } from '@chakra-ui/react'
import {FormControl, FormLabel,FormErrorMessage,FormHelperText,Input} from '@chakra-ui/react'
import { Center } from "@chakra-ui/react"
import  axios  from "axios";
import history from "History";



function Login() {
    const [email,setEmail]= useState();
    const [password,setPassword] =useState();
    const toast = useToast();
    
    const submihandler= async()=>{ 

      if (!password || !email) {
        toast({
          title: "Please fill all the fields!",
          status: "warning",
          duration: 4000,
          isClosable: true,
          position: "bottom",
        });
        // setLoading(false);
        return;
      }
 
      try {
        const config={
          headers :{
            "Content-type" : "application/json",
          }
        }
        const {data} =await axios.post(`http://localhost:6000/login`, {email,password},config )

        console.log(data);
        history.push("/adminpanal")
      } catch (error) {
        console.log(error);
        toast({
          title: "Login api call nhi ho rhi h",
          status: "error",
          duration: 5000,
          isClosable: true,
          position: "bottom",
        });
      }
      
    }

  return (
   <Center >
     <VStack spacing="5px" width={"50%"} backgroundColor ={"blanchedalmond"} marginTop={'40px'} padding={'10px'} >
        <FormControl>
             <FormLabel id='email' >Email </FormLabel>
               <Input placeholder="Enter your email" onChange={(e)=>  setEmail(e.target.value)} /> 
        </FormControl>
        <FormControl>
             <FormLabel id='password' >Password </FormLabel>
               <Input placeholder="Enter your password" onChange={(e)=>  setPassword(e.target.value)} /> 
        </FormControl>
        <Button width={"100%"}  colorScheme='blue' margin={"15px"} onClick={submihandler} >
           login
        </Button>
    </VStack>
   </Center>
  )
}

export default Login
