import React from 'react'
import { useState } from 'react'
import { Stack, HStack, VStack, Button, useToast } from '@chakra-ui/react'
import {FormControl, FormLabel,FormErrorMessage,FormHelperText,Input} from '@chakra-ui/react'
import { Center } from "@chakra-ui/react"
import  axios  from "axios"
import { useHistory } from "react-router-dom"

function Login() {
    const [email,setEmail]= useState();
    const [password,setPassword] =useState();
    const toast = useToast();
    
    // const history=useHistory();
    const submihandler = async () => {
      if (! password || ! email) {
        toast({
          title: "Please fill all the fields!",
          status: "warning",
          duration: 4000,
          isClosable: true,
          position: "bottom",
        });
        return;
      }
    
      try {
        const config = {
          headers: {
            "Content-type": "application/json",
          },
        };
        const { data } = await axios.post(
          `http://localhost:5000/api/user/login`, // Change to HTTPS
          { email, password },
          config
        );
        
        console.log("Login Successful");
        console.log(data); // Assuming your server returns some data on successful login
        // Redirect to appropriate page or perform other actions here
      } catch (error) {
        console.error("Axios Error:", error);
        toast({
          title: "Login API call failed",
          status: "error",
          duration: 5000,
          isClosable: false,
          position: "bottom",
        });
      }
    };
    
    
      
    

  return (
   <Center >
     <VStack spacing="5px" width={"50%"} backgroundColor ={"blanchedalmond"} marginTop={'40px'} padding={'10px'} >
        <FormControl>
             <FormLabel id='email' >Email </FormLabel>
               <Input placeholder="Enter your email" onChange={(e)=>  setEmail(e.target.value)} /> 
        </FormControl>
        <FormControl>
             <FormLabel id='password' >Password </FormLabel>
               <Input placeholder="Enter your password" onChange={(e)=>  setPassword(e.target.value)} /> 
        </FormControl>
        <Button width={"50%"}  colorScheme='blue' margin={"15px"} onClick={submihandler} >
           login
        </Button>
    </VStack>
   </Center>
  )
}

export default Login;
