import React from 'react'
import { useState } from 'react'
import { Stack, HStack, VStack, Button, useToast, Text } from '@chakra-ui/react'
import {FormControl, FormLabel,FormErrorMessage,FormHelperText,Input} from '@chakra-ui/react'
import { Center } from "@chakra-ui/react"
import  axios  from "axios"
import { useNavigate } from 'react-router-dom'

function Login() {

    const [email,setEmail]= useState();
    const [password,setPassword] =useState();
    const toast = useToast();
    const  navigate =useNavigate();
    // const history=useHistory();
    const submihandler = async () => {
      // event.preventDefault();

      axios
        .post('http://localhost:5000/login', { email, password })
        .then((response) => {
          const { data } = response;
          console.log(data);
          if (data.message === 'Login successfull') {
            navigate('/adminpanal');
            alert('Login Successful');
          } else {
            alert('Invalid email or password');
          }
        })
        .catch((error) => {
          console.error(error);
          alert('Invalid email or password');
        });
    
      }  
    

  return (
   <Center >
     <VStack spacing="5px" width={"50%"} backgroundColor ={"blanchedalmond"} marginTop={'40px'} padding={'10px'} >
                 <Text > Admin login</Text>
        <FormControl>
             <FormLabel id='email' >Email </FormLabel>
               <Input placeholder="Enter your email" onChange={(e)=>  setEmail(e.target.value)} /> 
        </FormControl>
        <FormControl>
             <FormLabel id='password' >Password </FormLabel>
               <Input placeholder="Enter your password" onChange={(e)=>  setPassword(e.target.value)} /> 
        </FormControl>
        <Button width={"50%"}  colorScheme='blue' margin={"15px"} onClick={submihandler} >
           login
        </Button>
    </VStack>
   </Center>
  )
}

export default Login;
